import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <Componentino />
      </header>
    </div>
  );
}

class Componentino extends React.Component {
  constructor(props){
    super(props);
    this.state={ora: new Date()};

    this.tick = this.tick.bind(this);
  }

  componentDidMount(){
    this.timerID = setInterval(this.tick, 1000);
  }

  componentWillUnmount (){
    clearInterval(this.timerID);
  }

  render() {
    return (<h1>Funziona porco cazzo: {this.state.ora.toLocaleTimeString()}</h1>);
  }

  tick (){
    this.setState({ora:new Date()});
  }
}

export default App;
