import React from 'react';
import logo from './logo.svg';
import './App.css';
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";

function App() {
  return (
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Button variant="contained" color="primary">
            DIOCANE
          </Button>
        </Grid>
        <Grid item xs={6}>
          <Button variant="contained" color="primary">
            DIOCANE
          </Button>
        </Grid>
        <Grid item xs={6}>
          <Button variant="contained" color="primary">
            DIOCANE
          </Button>
        </Grid>
      </Grid>
  );
}

class Componentino extends React.Component {
  constructor(props){
    super(props);
    this.state={ora: new Date()};

    this.tick = this.tick.bind(this);
  }

  componentDidMount(){
    this.timerID = setInterval(this.tick, 1000);
  }

  componentWillUnmount (){
    clearInterval(this.timerID);
  }

  render() {
    return (<h1>Funziona porco cazzo: {this.state.ora.toLocaleTimeString()}</h1>);
  }

  tick (){
    this.setState({ora:new Date()});
  }
}

export default App;
